import React from 'react';

import MaskInput from 'react-maskinput';

import {Link,useHistory} from 'react-router-dom'

import './styles.css'

import {FiChevronLeft,FiChevronRight}from 'react-icons/fi';

import {MdInfo}from 'react-icons/md';

import {IoIosCheckmarkCircle}from 'react-icons/io';

import CardImg from '../../assets/FrontCardEmpty.svg'; 

import CardFilledImg from '../../assets/FronCard.svg'; 

import VisaImg from '../../assets/Visa.png'; 

import iconImg  from '../../assets/IconCard.svg';

import iconImgtwo  from '../../assets/2-circle.gif';

import iconImgthree  from '../../assets/3-circle.gif';



function Pagamento()  {

  const history = useHistory();

  const [onChange, setOnChange] = React.useState('');
  // const [onChangee, setOnChange] = React.useState('');
  const [onValueChange, setOnValueChange] = React.useState('');

  async function handleLogin(e){

      e.preventDefault();

      try {  

      //Feito para guardar em variavel e utilizar pelo site todo 
      // localStorage.setItem('ongid',id);
      // localStorage.setItem('ongName',response.data.name);


      history.push('/pagamento');

      //alert('falha no login');


      } catch (err) {
          alert('Falha ao Tenatar efetuar o pagamento');
      }

  }

  return (
    <div className="pagamento-container">
      <div className="content">
        <section>

          <Link className="back-linkPagamento" to="/">
            <FiChevronLeft  size={16} color="#FFFFF" />
                        Alterar Forma de Pagamento
          </Link>

          <div className="content-top-mobile"> 

          <Link to="/">
            <FiChevronLeft size={16} color="#DE4B4B" />    
          </Link>  

            <span id="etapa">Etapa 2 </span>        
            <span id="etapaAfter">de 3</span>

          </div>

          <div className="content-text">

            <img src={iconImg} alt="Card"/>
            <span >Adicione um novo cartão de crédito</span>

          </div>

          <div className="cardEmpty">
            <p>**** **** **** ****</p>
            <p id="titular">NOME DO TITULAR</p>
            <p id="dateVal">00/00</p>
            <img id="cardFront" src={CardImg} alt="IMAGEM CARTÃO VAZIO"/>
          </div>
          
          <div className="cardFilled" >
            <img id="imgVisa" src={VisaImg} alt="CARTÃO VISA"/>
            <p id="numberCard">{onChange}</p>
            <p id="titular"></p>
            <p id="dateVal">{onValueChange}</p>
            <img id="cardFront" src={CardFilledImg} alt="IMAGEM CARTÃO PREENCHIDO"/>
          </div>                

        </section>

        <div className="content-form">

          <div className="content-top">

            <IoIosCheckmarkCircle size={16} color="#DE4B4B" />          
            <span >Carrinho</span>

            <FiChevronRight size={16} color="#DE4B4B" />
            <img src={iconImgtwo} alt="step pagamento"/>
            <span >Pagamento</span>

            <FiChevronRight size={16} color="#DE4B4B" />
            <img src={iconImgthree} alt="step confirmação"/>
            <span >Confirmação</span>

          </div>

          {/* <form onSubmit={handleRegister}> */}
          <form >     

            <div className="input-container">
              <MaskInput
                  onChange={e => setOnChange(e.target.value)}
                  
                  mask={'0000 0000 0000 0000'}
                  value={onChange}
                  size={20}
                  // placeholder="Numero do Cartão"
                  id="name"
                  className="input"
                  type="text"
                  pattern=".+"
                  required
              />
              
              <label className="label" htmlFor="name">Numero do Cartão</label>
            </div>

            <div className="input-container">
              <input id="name"  className="input" type="text" pattern=".+" required />
              <label className="label" htmlFor="name">Nome (igual ao cartão)</label>
            </div>

            <div className="input-group">             
              <div className="input-container">
              <MaskInput
                  onChange={e => setOnChange(e.target.value)}
                  onValueChange={setOnValueChange}
                  mask={'00/00'}
                  value={onChangee}
                  size={20}
                  // placeholder="Numero do Cartão"
                  id="name"
                  className="input"
                  type="text"
                  pattern=".+"
                  required
              />
                {/* <input id="name" className="input valInput" type="text" pattern=".+" required /> */}
                <label className="label" htmlFor="name">Validade</label>
              </div>
              <div className="input-container">
                  <input id="name" maxLength='3' className="input" type="text" pattern=".+" required />
                  <label className="label" htmlFor="name">CVV  <MdInfo size={16} color="#FFFFF" alt="step pagamento"/></label>
                
              </div>
            </div>

            <div className="input-container last-container">
              <input id="name" className="input" type="text" pattern=".+" required />
              <label className="label" htmlFor="name">Numero de Parcelas</label>
            </div>

            <button className="buttonPagamento" type="submit">CONTINUAR</button>

          </form>
        </div>        
      </div>
  </div>
  );
}

export default Pagamento;
