export function ValidateNumberCard(numberCard){

    var vMessenger = 'Númbero do Cartão Valido';

    var vValid = false;

    if (numberCard.length === 19)
    {
        var sum = 0;

        var val = numberCard.trim().replace(" ","").replace(" ","").replace(" ","");

        for (var i = 0; i < val.length; i++) {

            var intVal = parseInt(val.substr(i, 1));

            if (i % 2 == 0) {
                intVal *= 2;
                if (intVal > 9) {
                    intVal = 1 + (intVal % 10);
                }
            }
            sum += intVal;
        }

        vValid = ((sum % 10) == 0);

        vMessenger = vValid === false ? 'Número do Cartão Inválido.' : vMessenger;
    

    }else{

        vMessenger = 'Número do Cartão Inválido.';
    }

    return {vMessenger,vValid}
}

export function ValidateDate(date) {

    var vMessenger = 'Data validade Válida.';

    var vValid= false;

    var val = date.replace("/","");

    var vYear = parseInt(val.substr(0,val.length - 2));    

    var today = new Date();

    var vMonth = parseInt(date.substr(0,2));

    var vDateMonth = today.getMonth()+1;
    var vDateYear = today.getFullYear();//  today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();

    if (vYear >= vDateYear && vYear < 2050)
    {        
        vValid = true;
    }
    else
    {
        vMessenger = 'Data validade Inválida';
    } 
    
    if(vValid && vMonth > 0 && vMonth < 13){

        if (vYear === vDateYear && vMonth < vDateMonth){
            vValid = false;
            vMessenger = 'Data Inválida';
        }else{

            vValid = true;
            vMessenger = 'Data validade Válida.';
        }
    }
    else
    {
        vValid = false;
        vMessenger = 'Data validade Inválida';
    }

    return {vMessenger,vValid}
}

export function ValidateName(name) {

    var vValid = !!name.match(/[A-Z][a-z]* [A-Z][a-z]*/);
    var vValidd = !!name.match(/^[A-ZÀ-Ÿ][A-zÀ-ÿ']+\s([A-zÀ-ÿ']\s?)*[A-ZÀ-Ÿ][A-zÀ-ÿ']+$/);

    return vValidd
}
    


export function ValidateCVV(pCVV) {

    var vMessenger = 'Código Inválido.';

    var vValid = false;

    if (pCVV.length === 3){

        vMessenger = 'Código válido.';
        
        vValid= true;
    }
    
    return {vMessenger,vValid}
}
