import React from 'react';

import './styles.css'



// import {FiLogIn}from 'react-icons/fi';

// import logoImg from '../../assets/LogoDemoShop.svg'; 

// import VisaImg  from '../../assets/FudooLogon.png';



function Confirmacao() {

  // const [items, setItems] = React.useState([]);

  const [loading, setLoading] = React.useState(true);


  const [items, setItems] = React.useState([
    { label: "Loading ...", value: "" }
  ]);

  const [value, setValue] = React.useState();

  React.useEffect(() => {

    let unmounted = false;

    async function getCharacters() {

      const response = await fetch("https://pokeapi.co/api/v2/pokemon/");
      const body = await response.json();
      if (!unmounted) {
        setItems(body.results.map(({ name }) => ({ label: name, value: name })));
  
        setLoading(false);
      }

    }
    getCharacters();
    return () => {
      unmounted = true;
    };
  }, []);


  // const [items] = React.useState([
  //   {
  //     label: "Luke Skywalker",
  //     value: "Luke Skywalker"
  //   },
  //   { label: "C-3PO", value: "C-3PO" },
  //   { label: "R2-D2", value: "R2-D2" }
  // ]);
  
  return (    
      
      <div className="content">
        <hi className="pag">Confirmacao </hi>
          <div className="con">
              <select disabled={loading} value={value}
              onChange={e => setValue(e.currentTarget.value)} >
              {items.map(({ label, value }) => (
                    <option key={value} value={value}>
                      {label}
                    </option>
                  ))}
              </select>

          </div>

      </div>   
     

  );
}

export default Confirmacao;
