import React from 'react';

//import CurrencyInput from './CurrencyInput'

import './styles.css'

import {useHistory} from 'react-router-dom';

import CardImg from '../../assets/FrontCardEmpty.svg'; 

function Carrinho() {

  const history = useHistory();

  const [valuePaymente, setOnvaluePaymente] = React.useState('e');

  // const handleChange = () =>{

  //   setOnvaluePaymente(

  //     ''
  //   )
  // }

  function increment(e) {

    var a = e.target.value;

    a = a.replace(",","")

    a= a.substr(0,a.length-2);

    setOnvaluePaymente(prevState => prevState+a);
  }

  async function handleValuePayment(e){

      e.preventDefault();

        var value;

      try {  

        value = valuePaymente;

        alert(value);

        localStorage.setItem('valuePayment',value);       


      //history.push('/pagamento');

      } catch (err) {
          alert('Falha ao Tenatar efetuar o pagamento');
      } 

  }

  return (

    <div className="logon-contender">

    <section>
   
    <img src={CardImg} alt="Demo Shop"/>  

        <form onSubmit={handleValuePayment} >

            <h1>Efetue o pagamento</h1>  

            <input
              onChange={increment}
              value={valuePaymente}
              type="text"
              name="name"
              placeholder="Digiteo valor"
            />

            <button className="button" type="submit" value="block" >Pagar</button>
            
        </form>
    </section>
</div>
  );
}

export default Carrinho;
