import React from 'react';

import MaskInput from 'react-maskinput';

import {Link} from 'react-router-dom'

import './styles.css'

import {FiChevronLeft}from 'react-icons/fi';

import {FiChevronRight}from 'react-icons/fi';

import {MdInfo}from 'react-icons/md';

import {IoIosCheckmarkCircle}from 'react-icons/io';

import CardImg from '../../assets/FrontCardEmpty.svg'; 

import iconImg  from '../../assets/IconCard.svg';

import iconImgtwo  from '../../assets/2-circle.gif';

import iconImgthree  from '../../assets/3-circle.gif';



function Pagamento()  {

  return (
    <div className="pagamento-container">
      <div className="content">
        <section>

          <Link className="back-linkPagamento" to="/">
            <FiChevronLeft size={16} color="#FFFFF" />
                        Alterar Forma de Pagamento
          </Link>

          <div className="content-text">

            <img src={iconImg} alt="Card"/>
            <span >Adicione um novo cartão de crédito</span>

          </div>
           
          <img src={CardImg} style={{paddingLeft:64,paddingTop:34}} alt="Demo Shop"/>           

        </section>

        <div className="content-form">

          <div className="content-top">

            <IoIosCheckmarkCircle size={16} color="#DE4B4B" />          
            <span >Carrinho</span>

            <FiChevronRight size={16} color="#DE4B4B" />
            <img src={iconImgtwo} alt="step pagamento"/>
            <span >Pagamento</span>

            <FiChevronRight size={16} color="#DE4B4B" />
            <img src={iconImgthree} alt="step confirmação"/>
            <span >Confirmação</span>

          </div>

          {/* <form onSubmit={handleRegister}> */}
          <form >
          <MaskInput alwaysShowMask maskChar=" " mask="0000-0000-0000-0000" size={20} />

            <div className="input-container">
              {/* <input id="name" maxLength='19' className="input" type="text" pattern=".+" required /> */}
              <MaskInput alwaysShowMask maskChar=" " mask="0000-0000-0000-0000" size={20} id="name"className="input"/>
              <label className="label" htmlFor="name">Numero do Cartão</label>
            </div>
            <div className="input-container">
              <input id="name"  className="input" type="text" pattern=".+" required />
              <label className="label" htmlFor="name">Nome (igual ao cartão)</label>
            </div>
            {/* <input 
            placeholder="Nome da ONG"
            value={name}
            onChange={e => setName(e.target.value)}
            /> */}
            {/* <input 
            placeholder="Numero do Cartão"

            /> */}
            {/* <input 
            type="email" 
            placeholder="E-mail"
            value={email}
            onChange={e => setEmail(e.target.value)}
            /> */}
            {/* <input         
            placeholder="Nome (igual ao cartão)"

            /> */}
            {/* <input 
            placeholder="WhatsApp"
            value={whatsapp}
            onChange={e => setWhatsapp(e.target.value)}
            /> */}

            <div className="input-group">

            {/* <input 
              placeholder="Cidade"
              value={city}
              onChange={e => setCity(e.target.value)}
            />
            <input 
              placeholder="UF" 
              style={{ width:80}}
              value={uf}
              onChange={e => setUf(e.target.value)}
            /> */}

            {/* <input 
              placeholder="Validade"
              
            />
            <input 
              placeholder="CVV" 
              
              
            /> */}
            <div className="input-container">
              <input id="name" className="input" type="text" pattern=".+" required />
              <label className="label" htmlFor="name">Validade</label>
            </div>
            <div className="input-container">
                <input id="name" maxLength='3' className="input" type="text" pattern=".+" required />
                <label className="label" htmlFor="name">CVV  <MdInfo size={16} color="#FFFFF" alt="step pagamento"/></label>
               
            </div>

            </div>

            <div className="input-container">
              <input id="name" className="input" type="text" pattern=".+" required />
              <label className="label" htmlFor="name">Numero de Parcelas</label>
            </div>


            <button className="buttonPagamento" type="submit">CONTINUAR</button>

          </form>


        </div>

        
      </div>
  </div>
  );
}

export default Pagamento;
