import React from 'react';
import CurrencyInput from 'react-currency-input';

import './styles.css'

import {useHistory} from 'react-router-dom';

import CardImg from '../../assets/FrontCardEmpty.svg'; 

function Carrinho() {

  const history = useHistory();

  async function handleLogin(e){

      e.preventDefault();

      try {  

      //Feito para guardar em variavel e utilizar pelo site todo 
      // localStorage.setItem('ongid',id);
      // localStorage.setItem('ongName',response.data.name);


      history.push('/pagamento');

      //alert('falha no login');


      } catch (err) {
          alert('Falha ao Tenatar efetuar o pagamento');
      } 

  }

  return (

    <div className="logon-contender">

    <section className="sectionClass">
   
    <img src={CardImg} alt="Demo Shop"/>        
        <form>

            <h1>Efetue o pagamento</h1>  

            <CurrencyInput  decimalSeparator="," thousandSeparator="." prefix="R$"  />   

            <button className="button" onClick={handleLogin} >Pagar</button>
            
        </form>
    </section>
</div>
  );
}

export default Carrinho;
